mapper.select:
SELECT count(*)
  FROM customer_info
 WHERE email=#{email}

SELECT count(*)
  FROM ceo_info
 WHERE email=#{email}

SELECT id, email, pw, name, address, phone, point, grade, rdate
  FROM customer_info
 WHERE email=#{email}

SELECT id, email, pw, name, address, phone
  FROM ceo_info
 WHERE email=#{email}

SELECT id, email, name, address, phone, point, grade, rdate
  FROM customer_info
 WHERE email=#{email} and pw=#{pw}

SELECT id, email, name, address, phone
  FROM ceo_info
 WHERE email=#{email} and pw=#{pw}

SELECT email
  FROM customer_info
 WHERE name=#{name} and phone=#{phone}

SELECT email
  FROM ceo_info
 WHERE name=#{name} and phone=#{phone}

mapper.update:
UPDATE customer_info
   SET pw=#{pw}
 WHERE email=#{email}

UPDATE ceo_info
   SET pw=#{pw}
 WHERE email=#{email}

mapper.select:
SELECT id, email, name, address, phone, point, grade, rdate
  FROM customer_info
 WHERE id=#{id}

SELECT id, email, name, address, phone
  FROM ceo_info
 WHERE id=#{id}
