mapper.select:
select id, name, price, url, product_rating
from product
where id=#{id}

mapper.update:
UPDATE product
SET product_rating = (
    SELECT AVG(rating)
    FROM review
    WHERE product_id = #{productId}
)
WHERE id = #{productId};

mapper.select:
select id, product_id as `productId`, customer_id as `customerId`, content, rating
from review 
where product_id=#{id}
order by id desc;
